import React, { useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button'

export default function PostBox() {
  const [value, setValue] = useState();

  const handleChange = (event) => {
    setValue(event.target.value);
  };

  console.log(value)

  return (
    <>
    <Box
      component="form"
      sx={{
        '& > :not(style)': { m: 1, width: '95%' },
        background: "moccasin"
      }}
      noValidate
      autoComplete="off"
    >
      <div id="user-title">
        <TextField id="title" label="Title" variant="outlined" value={value} fullWidth onChange={handleChange}/>
        <div id="divider"/>
        <TextField id="username" label="Username" variant="outlined" value={value} onChange={handleChange}/>
      </div>
    </Box>
    <Box
    component="form"
    sx={{
      '& > :not(style)': { m: 1.5, width: '98%' },
      background: "moccasin"
    }}
    noValidate
    autoComplete="off"
    >
    <TextField
        id="content"
        label="Write something for the world!"
        multiline
        rows={3}
        value={value}
        onChange={handleChange}
        />
    </Box>
    <Button variant="contained" fullWidth sx={{marginTop: 1.5}}>Post it!</Button>
    </>
  );
}
